// Procesar respuesta del AI Agent y formatear para VoIP
const userText = $input.first()?.json?.text || '';
const agentResponse = $input.first()?.json?.output || 'Lo siento, no pude procesar tu solicitud.';
const toolsUsed = $input.first()?.json?.toolCalls || [];

// Log para debugging
console.log('Input del usuario:', userText);
console.log('Respuesta AI Agent:', agentResponse);
console.log('Herramientas utilizadas:', toolsUsed);

// Procesar respuesta del agente
let finalResponse = agentResponse;

// Si se usaron herramientas, agregar confirmación
if (toolsUsed.length > 0) {
  toolsUsed.forEach(tool => {
    if (tool.tool === 'agendar_cita') {
      finalResponse += ' Tu cita ha sido registrada.';
    } else if (tool.tool === 'cancelar_cita') {
      finalResponse += ' Tu cita ha sido cancelada.';
    }
  });
}

// Limitar longitud de respuesta para VoIP (máximo 200 caracteres)
if (finalResponse.length > 200) {
  finalResponse = finalResponse.substring(0, 197) + '...';
}

// Detectar intención para analytics
function getIntent(text) {
  const lowerText = text.toLowerCase();
  if (lowerText.includes('cita') || lowerText.includes('agendar')) return 'schedule';
  if (lowerText.includes('cancelar')) return 'cancel';
  if (lowerText.includes('horario') || lowerText.includes('cuándo')) return 'hours';
  if (lowerText.includes('servicio') || lowerText.includes('precio')) return 'services';
  if (lowerText.includes('adiós') || lowerText.includes('gracias')) return 'goodbye';
  return 'general';
}

return {
  json: {
    response: finalResponse,
    user_input: userText,
    intent_detected: getIntent(userText),
    tools_used: toolsUsed.map(t => t.tool),
    timestamp: new Date().toISOString(),
    conversation_context: 'voip_call'
  }
};
